import React, { useRef, useEffect, useCallback } from 'react';
import { useSpring, animated } from 'react-spring';
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { MdClose } from 'react-icons/md';
import { logout, reset } from '../../features/auth/authSlice'
import { connect } from '../../features/spotify/spotifySlice'
import { useState } from 'react';

//holds everything
const Container = styled.div`
    display:flex;
    align-items: center;
    justify-content: center;
    height: 100vh;
    gap: 2rem;
    position: relative;
`;

const Background = styled.div`
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
`;

//image for modal
const ModalImg = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 10px 0 0 10px;
  background: #000;
  position: relative;

  /* Extra small devices*/
  @media only screen and (max-width: 590px) {
    display: none;
  }

`;

const ModalWrapper = styled.div`
  margin: auto;
  box-shadow: 0 5px 16px rgba(0, 0, 0, 0.2);
  background: #fff;
  color: #000;
  display: grid;
  grid-template-columns: 1fr 1fr;
  position: relative;
  border-radius: 10px;
  object-fit: fill;

  /* EXTRA extra small devices */
  @media only screen and (max-width: 379px) {
    width: 300px;
  }
  /* Extra small devices (600px and down) */
  @media only screen and (min-width: 380px) and (max-width: 600px) {
  width: 350px;
  }

  /* Small devices (portrait tablets and large phones, 600px and up) */
  @media only screen and (min-width: 600px) {
    width: 580px;
  }

  /* Medium devices (landscape tablets, 768px and up) */
  @media only screen and (min-width: 768px) {
    width: 650px;
  }

  /* Large devices (laptops/desktops, 992px and up) */
  @media only screen and (min-width: 992px) {
    width: 700px;
  }

  /* Extra large devices (large laptops and desktops, 1200px and up) */
  @media only screen and (min-width: 1200px) {
    width: 1000px;
  
  }
`;


const Greeting = styled.p`

  margin-bottom: 1rem;
  text-align: center;

  /* EXTRA extra small devices */
  @media only screen and (max-width: 379px) {
  font-size:15px;
  width: 300px;
  }
  
  /* Extra small devices (phones, 600px and down) */
  @media only screen and (min-width: 380px) and (max-width: 600px) {
  font-size:15px;
  width: 350px;
  }

`;

const SpotifyMessage = styled.h1`

  margin-bottom: 1rem;
  text-align: center;
  position: relative;

  /* EXTRA extra small devices */
  @media only screen and (max-width: 379px) {
    font-size:20px;
  }
  
  /* Extra small devices (phones, 600px and down) */
  @media only screen and (min-width: 380px) and (max-width: 600px) {
  font-size:20px;
  }
`;

const ModalContent = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: #141414;
  object-fit: fill;
  padding: 5%;

  button {
    width: 70%;
    padding: 10px 24px;
    background: #1DB954;
    color: #fff;
    border: none;
    border-radius: 10px;
    margin-bottom: 2%;
  }
  a {
      width: 70%;
      padding: 10px 24px;
      text-decoration: none;
      border: none;
      border-radius: 10px;
      margin-bottom: 2%;
    }
 .connect-button {
    width: 75%;
    padding: 11px;
    font-size: 15px;
    color: #fff;
    font-weight: 500;
    border: none;
    background: #1DB954;
    border-radius: 15px;
    cursor: pointer;
    transition: all, 240ms ease-in-out;
    align-self: center;
    text-shadow: 0 0 2px #999;

    /* Extra small devices (phones, 600px and down) */
    @media only screen and (min-width: 320px) and (max-width: 379px) {
    width:200px;
    }
  }
  .connect-button:hover {
    background: #1db954;
    filter: brightness(1.2);
  }
  .logout-button {
    width: 75%;
    padding: 11px;
    font-size: 15px;
    color: white;
    font-weight: 500;
    border: none;
    background: #CDBEE0;
    border-radius: 15px;
    cursor: pointer;
    transition: all, 240ms ease-in-out;
    background: var(--purple);
    align-self: center;
    text-shadow: 0 0 2px #999;

    /* EXTRA extra small devices */
    @media only screen and (min-width: 320px) and (max-width: 379px) {
    width:200px;
    }

  }
.logout-button:hover {
    background: rgba(93, 48, 149, 1);
    filter: brightness(1.2);
  }
`;

const CloseModalButton = styled(MdClose)`
  cursor: pointer;
  position: absolute;
  top: 20px;
  right: 20px;
  width: 32px;
  height: 32px;
  padding: 0;
  z-index: 10;
`;


export const Modal = () => {
  const [showModal, setShowModal] = useState(true);
  const { user } = useSelector((store) => store.auth)
  const modalRef = useRef();

  const navigate = useNavigate();
  const dispatch = useDispatch();
  const animation = useSpring({
    config: {
      duration: 250
    },
    opacity: showModal ? 1 : 0,
    transform: showModal ? `translateY(0%)` : `translateY(-100%)`
  });

  const onLogout = () => {
    setShowModal(false);

    dispatch(logout())
      .then(() => {
        navigate("/", { replace: true })
      })
      .then(() => {
        dispatch(reset())
      })
  }

  const onClick = (e) => {
    e.preventDefault()

    // window.open('http://localhost:5555/api/spotify/connect', '_self');
    // window.location.href = 'http://localhost:5555/api/spotify/connect'

    dispatch(connect())
    // .then(() => {
    //     navigate("/home", { replace: true})
    // })
    return true
  }

  return (
    <>
      {showModal ? (
        <Container>
          <Background ref={modalRef}>
            <animated.div style={animation}>
              <ModalWrapper showModal={showModal}>
                <ModalImg src={require('./spotify_modal4.png')} alt='camera' />
                <ModalContent>
                  <Greeting>Hey, {user.name}</Greeting>
                  <SpotifyMessage>You are not connected to Spotify!</SpotifyMessage>
                  <Greeting>Please connect to use the full features of Anthem</Greeting>
                  <a className="button connect-button" href="http://anthem-cop4331.herokuapp.com/api/spotify/connect" target="_self" >Connect to Spotify</a>
                  {/* <button className="button connect-button" onClick={onClick} >2. Connect to Spotify</button> */}
                  <button className="button logout-button" onClick={onLogout}>Logout</button>
                </ModalContent>
              </ModalWrapper>
            </animated.div>
          </Background>
        </Container>
      ) : ""}
    </>
  );
};
